    const { locale } = useContext(ContextApi);
    console.log(locale, 'locale');
    const SkillData = [
        {
          skillIcon: 'https://image.flaticon.com/icons/png/512/174/174854.png',
          skillName: 'HTML',
          skillInfo: locale.lang,
