import React, {useContext} from 'react';
import { ContextApi } from '../ContextApi';

function Setting() {
    const { setLang, locale } = useContext(ContextApi);
  return (
    <div>
      <div className="container">
        <div className="row">
          <h1>{locale.lang}:</h1>
          <div>
            <button type="button" className="btn btn-outline-dark btn-sm" onClick={()=>setLang('zh')}>
              {locale.zh}
            </button>
            <button type="button" className="btn btn-outline-dark btn-sm m-1" onClick={()=>setLang('en')}>
              {locale.en}
            </button>
          </div>

          <h1>{locale.theme}:</h1>
          <div>
            <button type="button" className="btn btn-outline-dark btn-sm">
              {locale.themeLight}
            </button>
            <button type="button" className="btn btn-outline-dark btn-sm m-1">
              {locale.themeDark}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Setting;
