import React, {useContext} from 'react';
import { ContextApi } from '../ContextApi';

function Setting() {
    const { setLang, locale, setTheme, theme, textTheme, bgTheme } = useContext(ContextApi);
    console.log(theme, 'theme')
  return (
    <div style={{backgroundColor: bgTheme, height: '90vh'}}>
      <div className="container">
        <div className="row">
          <h1>{locale.lang}:</h1>
          <div>
            <button type="button" className="btn btn-outline-dark btn-sm" onClick={()=>setLang('zh')}>
              {locale.zh}
            </button>
            <button type="button" className="btn btn-outline-dark btn-sm m-1" onClick={()=>setLang('en')}>
              {locale.en}
            </button>
          </div>

          <h1>{locale.theme}:</h1>
          <div>
            <button type="button" className="btn btn-outline-dark btn-sm" onClick={()=>setTheme('light')}>
              {locale.themeLight}
            </button>
            <button type="button" className="btn btn-outline-dark btn-sm m-1" onClick={()=>setTheme('dark')}>
              {locale.themeDark}
            </button>
            <h1 style={{color: textTheme}}>測試</h1>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Setting;
