import React, { useContext } from 'react';
import Typewriter from 'typewriter-effect';
import { ContextApi } from '../ContextApi';
import { Link } from 'react-router-dom';
import '../style/index.css';

function MainPage() {
  const { locale, bgTheme, textTheme } = useContext(ContextApi);
  return (
    <div style={{ height: '93.7vh', backgroundColor: bgTheme }}>
      <div className="container-fluid" style={{ height: '80vh' }}>
        <div className="row" style={{ border: '1px solid red' }}>
          <section class="py-5 text-center container">
            <div class="row py-lg-5">
              <div class="col-lg-6 col-md-8 mx-auto">
                <h1 class="fw-light d-flex justify-content-center">
                  <div className="typewriterWidth">
                    <h1 style={{ color: textTheme }}>
                      <Typewriter
                        options={{
                          strings: [
                            locale.myName,
                            locale.FrontEndEngineer,
                          ],
                          autoStart: true,
                          loop: true,
                        }}
                      />
                    </h1>
                  </div>
                </h1>
                <p class="lead text-muted">
                  Something short and leading about the collection below—its
                  contents, the creator, etc. Make it short and sweet, but not
                  too short so folks don’t simply skip over it entirely.
                </p>
                <p>
                  <a href="#" class="btn btn-primary my-2">
                    Main call to action
                  </a>
                  <a href="#" class="btn btn-secondary my-2">
                    Secondary action
                  </a>
                </p>
              </div>
            </div>
          </section>
          {/* <div className="typewriterWidth">
            <h1 style={{color:textTheme}}>{locale.hello}</h1>

            <h1 style={{color:textTheme}}>{locale.myName}</h1>
            <h1 style={{color:textTheme}}>{locale.iAm}</h1>
            <h1 style={{color:textTheme}}>
              <Typewriter
                options={{
                  strings: [locale.FrontEndEngineer, locale.DivingLover],
                  autoStart: true,
                  loop: true,
                }}
              />
            </h1>
          </div> */}
        </div>
      </div>
    </div>
  );
}
export default MainPage;
