import Typewriter from 'typewriter-effect';
import { ContextApi } from '../ContextApi';
import { Link } from 'react-router-dom';
import '../style/index.css';

function MainPage() {
  const { locale, bgTheme, textTheme } = useContext(ContextApi);
  return (
    <div style={{ height: '93.7vh', backgroundColor: bgTheme }}>
      <div
        className="container d-flex justify-content-center align-items-center"
        style={{ height: '80vh' }}
      >
        <div className="row d-flex justify-content-center">
          <div className="col-sm-6" style={{ border: '1px solid red' }}>
            <div class="card">
              <img src="..." class="card-img-top" alt="..." />
              <div class="card-body">
                <h5 class="card-title">Card title</h5>
                <p class="card-text">
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </p>
                <Link to="/products">
                  <button href="#" class="btn btn-primary">
                    查看專案
                  </button>
                </Link>
              </div>
            </div>
          </div>
          {/* <div className="typewriterWidth">
            <h1 style={{color:textTheme}}>{locale.hello}</h1>

